 FUNCTION "LGF_StringToInt" : DInt
{ S7_Optimized_Access := 'TRUE' }
AUTHOR : Siemens_Digital_Industry
FAMILY : LGF
NAME : LGF_StringToInt
   VAR_INPUT 
      value : String;
   END_VAR

   VAR_TEMP 
      tempDoubleInteger : DInt;
   END_VAR

   VAR CONSTANT 
      CONVERT_FORMAT_TO_INTEGER : Word := 16#0000;
      CONVERT_START_POSITION : UInt := 1;
   END_VAR


BEGIN
	REGION BLOCK INFO HEADER
	  //===============================================================================
	  // SIEMENS AG / (c)Copyright 2019
	  //-------------------------------------------------------------------------------
	  // Title:            LGF_StringToInt
	  // Comment/Function: 此函数将字符串转换为整数值
	  //                   LGF_IntToString的逆向 - 系统函数VAL_STRG的封装。
	  // Library/Family:   LGF(库通用函数)
	  // Author:           Siemens Digital Industry Support
	  // Tested with:      
	  // Engineering:      TIA Portal V15.1
	  // Restrictions:     ENO处理由STRG_VAL完成
	  // Requirements:     PLC (S7-1200 / S7-1500)
	  //-------------------------------------------------------------------------------
	  //===============================================================================
	END_REGION Block info header

	REGION CONVERSION
	  STRG_VAL(IN     := #value,
	           FORMAT := #CONVERT_FORMAT_TO_INTEGER,
	           P      := #CONVERT_START_POSITION,
	           OUT    => #tempDoubleInteger);
	END_REGION

	REGION OUTPUT
	  #LGF_StringToInt := #tempDoubleInteger;
	END_REGION

END_FUNCTION
